    List<ActivityValue> values;

    async Task Add()
    {
        var navigation = values.Where(c => c.Id == act.ActivityFeature).FirstOrDefault();
        act.UserId = _userId;
        act.TotalCalorie = CalculateCalorie(navigation.Point, act.Hour);
